                return this.carouselItems.length;
            const dimension = this.getDimension();
            const carouselDimension = this.carouselCore.nativeElement.getBoundingClientRect()[dimension];
            let counter = this.currentIndex;
            let itemsDimension = 0;
            while (itemsDimension < carouselDimension && counter < this.carouselItems.length) {
                itemsDimension += this.carouselItemsArray[counter].elRef.nativeElement.getBoundingClientRect()[dimension];
                counter++;
            }
            return Math.max(0, counter - this.currentIndex - 1);
        };
